import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import LinkIcon from '@mui/icons-material/Link';
import moment from 'moment';
import Chip from '@mui/material/Chip';

const RelevantFacts = (props) => {
    console.log(props)
    if(props.facts === undefined){
        return (
            <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
            <ListItem alignItems="center">
              <ListItemAvatar>
                <Avatar alt="N" src="/static/images/avatar/1.jpg" />
              </ListItemAvatar>
              <ListItemText>No relevant results</ListItemText>
            </ListItem>
          </List>
        )
    }
    else return (
    <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>

      {props.facts.map(fact=><ListItem alignItems="flex-start" key={fact.claimant}>
        <ListItemAvatar>
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary={"Claim by " + fact.claimant}
          secondary={
            <React.Fragment>
              {fact.text}
              <br /><br />
              {fact.claimReview.length > 0 && fact.claimReview.map(review =>
              <>
                <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="subtitle2"
                color="text.primary"
                key={review.publisher.site}>
                  {review.publisher.name}
                  {"    "} <Chip variant="filled" label={review.textualRating} />
                </Typography>
                <br />

                <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="body2"
                color="text.primary"
                key={review.publisher.site}>
                  <a key={review.url} href={review.url} style={{ textDecoration: "none"}}>{review.title}</a>
                </Typography>
                <br />

                <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="caption"
                color="text.primary"
                key={fact.claimDate}>
                {moment(fact.claimDate).format('MMM D, YYYY')}
                </Typography>

              </>)}
            </React.Fragment>
          }
        />
      </ListItem>)
      }


      <Divider variant="inset" component="li" />
    </List>
  );
}

export default RelevantFacts;
