import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import moment from 'moment';
import { v4 as uuidv4 } from 'uuid';

const RelevantFacts = (props) => {
    if(props.facts === undefined){
        return (
            <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
            <ListItem alignItems="center">
              <ListItemAvatar>
                <Avatar alt="N" src="/static/images/avatar/1.jpg" />
              </ListItemAvatar>
              <ListItemText>No relevant results</ListItemText>
            </ListItem>
          </List>
        )
    }
    else return (
    <List sx={{  bgcolor: 'background.paper' }}>

      {props.facts.map(fact=>fact.claimReview[0].languageCode === "en" && <ListItem alignItems="flex-start" key={uuidv4()}>
        <ListItemAvatar>
          <Avatar alt={fact.claimant} src="#" />
        </ListItemAvatar>
        <ListItemText
          primary={fact.claimant ? "Claim by " + fact.claimant : ""}
          secondary={
            <React.Fragment>
              {fact.text}
              <br /><br />
              {fact.claimReview.length > 0 && fact.claimReview.map(review =>
              <React.Fragment key={uuidv4()}>
                <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="subtitle2"
                color="text.primary"
                key={uuidv4()}>
                  {review.publisher.name}
                </Typography>
                {"      "} <span style={{
                  display: "inline-block",
                  padding: "2px 10px",
                  fontSize: "13px",
                  borderRadius: "25px",
                  backgroundColor: "#f1f1f1",
                  color:"black"
                }}>
                  {review.textualRating}
                </span>
                <br />

                <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="body2"
                color="text.primary"
                key={uuidv4()}>
                  <a key={uuidv4()} href={review.url} style={{ textDecoration: "none"}}>{review.title}</a>
                </Typography>
                <br />

                <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="caption"
                color="text.primary"
                key={uuidv4()}>
                {moment(fact.claimDate).format('MMM D, YYYY')}
                </Typography>

              </React.Fragment>)}
            </React.Fragment>
          }
        />
      </ListItem>)
      }


      <Divider variant="inset" component="li" />
    </List>
  );
}

export default RelevantFacts;
