import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import moment from 'moment';
import Chip from '@mui/material/Chip';
import { v4 as uuidv4 } from 'uuid';

const RelevantFacts = (props) => {
    if(props.facts === undefined){
        return (
            <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
            <ListItem alignItems="center">
              <ListItemAvatar>
                <Avatar alt="N" src="/static/images/avatar/1.jpg" />
              </ListItemAvatar>
              <ListItemText>No relevant results</ListItemText>
            </ListItem>
          </List>
        )
    }
    else return (
    <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>

      {props.facts.map(fact=><ListItem alignItems="flex-start" key={uuidv4()}>
        <ListItemAvatar>
          <Avatar alt={fact.claimant} />
        </ListItemAvatar>
        <ListItemText
          primary={fact.claimant ? "Claim by " + fact.claimant : ""}
          secondary={
            <React.Fragment>
              {fact.text}
              <br /><br />
              {fact.claimReview.length > 0 && fact.claimReview.map(review =>
              <>


              </>)}
            </React.Fragment>
          }
        />
      </ListItem>)
      }


      <Divider variant="inset" component="li" />
    </List>
  );
}

export default RelevantFacts;
